class VideoDetailMo {
  late bool isFavorite;
  late bool isLike;
  late VideoModel videoModel;
  late List<VideoModel> videoList;

  VideoDetailMo({
      required this.isFavorite,
      required this.isLike,
      required this.videoModel,
      required this.videoList});

  VideoDetailMo.fromJson(dynamic json) {
    isFavorite = json["isFavorite"];
    isLike = json["isLike"];
    videoModel = new  VideoModel.fromJson(json["videoInfo"]) ;
    if (json["videoList"] != null) {
      videoList = [];
      json["videoList"].forEach((v) {
        videoList.add(VideoModel.fromJson(v));
      });
    }
  }

  Map<String, dynamic> toJson() {
    var map = <String, dynamic>{};
    map["isFavorite"] = isFavorite;
    map["isLike"] = isLike;
    if (videoModel != null) {
      map["videoInfo"] = videoModel.toJson();
    }
    if (videoList != null) {
      map["videoList"] = videoList.map((v) => v.toJson()).toList();
    }
    return map;
  }

}

