  late int duration;
  late Owner owner;
  late int reply;
  late int favorite;
  late int like;
  late int coin;
  late int share;
  late String createTime;
  late int size;

  VideoInfo({
      required this.id,
      required this.vid,
      required this.title,
      required this.tname,
      required this.url,
      required this.cover,
      required this.pubdate,
      required this.desc,
      required this.view,
      required this.duration,
      required this.owner,
      required this.reply,
      required this.favorite,
      required this.like,
      required this.coin,
      required this.share,
      required this.createTime,
      required this.size});

  VideoInfo.fromJson(dynamic json) {
    id = json["id"];
    vid = json["vid"];
    title = json["title"];
    tname = json["tname"];
    url = json["url"];
    cover = json["cover"];
    pubdate = json["pubdate"];
    desc = json["desc"];
    view = json["view"];
    duration = json["duration"];
    owner = new  Owner.fromJson(json["owner"]) ;
    reply = json["reply"];
    favorite = json["favorite"];
    like = json["like"];
    coin = json["coin"];
    share = json["share"];
    createTime = json["createTime"];
    size = json["size"];
  }

  Map<String, dynamic> toJson() {
    var map = <String, dynamic>{};
    map["id"] = id;
    map["vid"] = vid;
    map["title"] = title;
    map["tname"] = tname;
    map["url"] = url;
    map["cover"] = cover;
    map["pubdate"] = pubdate;
    map["desc"] = desc;
    map["view"] = view;
    map["duration"] = duration;
    if (owner != null) {
      map["owner"] = owner.toJson();
    }
    map["reply"] = reply;
    map["favorite"] = favorite;
    map["like"] = like;
    map["coin"] = coin;
    map["share"] = share;
    map["createTime"] = createTime;
    map["size"] = size;
    return map;
  }

}
